function autocomplete(a){a.autocomplete({source:function(a,b){$.ajax({url:"http://ws.geonames.org/searchJSON",dataType:"jsonp",data:{featureClass:"P",style:"full",maxRows:12,name_startsWith:a.term,country:"US"},success:function(a){b($.map(a.geonames,function(a){return{label:a.name+(a.adminCode1?", "+a.adminCode1:"")+", "+a.countryCode,value:a.name+(a.adminCode1?", "+a.adminCode1:"")+", "+a.countryCode}}))}})},minLength:2,select:function(a,b){},open:function(){$(this).removeClass("ui-corner-all").addClass("ui-corner-top")},close:function(){$(this).removeClass("ui-corner-top").addClass("ui-corner-all")}})}function gmaps_autocomplete(a){var b=new google.maps.Geocoder;$(function(){a.autocomplete({source:function(a,c){b.geocode({address:a.term},function(a,b){c($.map(a,function(a){return{label:a.formatted_address,value:a.formatted_address,viewport:a.geometry.viewport,location:a.geometry.location}}))})},minLength:3})})}